DECLARE @datos_carpeta_trib TABLE  (
	Tipo     INT,
	orden    INT, 
	stytr    INT,
	[numero_scr] [int] NULL,
	[Mes] [int] NULL,
	[Anio] [int] NULL,
	[Periodo] [varchar](max) NULL,
	[lineaPeriodo] [varchar](max) NULL,
	[lineaCredito] [varchar](max) NULL,
	[lineaTotalCredito] [varchar](max) NULL,
	[lineaDebito] [varchar](max) NULL,
	[lineaFacturas] [varchar](max) NULL,
	[Credito] [numeric](20, 7) NULL,
	CreditoxFactor [numeric](20, 7) NULL,
	[Debito] [numeric](20, 7) NULL,
	DeditoxFactor [numeric](20, 7) NULL,
	FlujoIngreso [numeric](20, 7) NULL,
	[Facturas] [int] NULL,
	[Fecha_Ingreso] [datetime] NULL,
	Nombre_Mes  VARCHAR(50)
)

INSERT INTO @datos_carpeta_trib 
SELECT 1 AS tipo, row_number() OVER (ORDER BY Anio asc , Mes asc) AS orden , row_number() OVER (ORDER BY Anio asc , Mes asc) % 2 AS stytr ,   numero_scr, Mes, Anio, Periodo, 
    lineaPeriodo, lineaCredito,
       lineaTotalCredito, lineaDebito, lineaFacturas, 
        isnull(Credito,0)  as Credito,  
       
       round(isnull(Credito,0)* @factor,0) AS CreditoxFactor,
       isnull(Debito,0)   AS Debito,
       round( isnull(Debito,0)* @factor,0) AS DebitoxFactor,
       round((isnull(Debito,0) - isnull(Credito,0))* @factor,0) AS FlujoIngreso,
       isnull(Facturas,0) AS Facturas, Fecha_Ingreso , 
       MesNombre = left(CASE WHEN Mes = 01 THEN 'Enero' 
				    WHEN Mes = 02 THEN 'Febrero'
				    WHEN Mes = 03 THEN 'Marzo'
				    WHEN Mes = 04 THEN 'Abril'
				    WHEN Mes = 05 THEN 'Mayo'
				    WHEN Mes = 06 THEN 'Junio'
				    WHEN Mes = 07 THEN 'Julio'
				    WHEN Mes = 08 THEN 'Agosto'
				    WHEN Mes = 09 THEN 'Septiembre'
				    WHEN Mes = 10 THEN 'Octubre'
				    WHEN Mes = 11 THEN 'Noviembre'
				    WHEN Mes = 12 THEN 'Diciembre' END,3)  
  FROM datos_carpeta_trib WHERE datos_carpeta_trib.numero_scr = @Num_Scr 
  ORDER BY datos_carpeta_trib.Anio , datos_carpeta_trib.Mes

